def load_secret_key():
    """
    Load SECRET_KEY from file, environment variable, or fallback to default.
    Priority: File > Environment Variable > Default (dev only)
    """
    secret_key_file = CONFIG_DIR / 'secret_key.txt'

    # Try to load from file (Docker/Production)
    if secret_key_file.exists():
        with open(secret_key_file, 'r') as f:
            return f.read().strip()

    # Try environment variable
    env_secret = os.getenv('DJANGO_SECRET_KEY')
    if env_secret:
        return env_secret

    # Fallback for local development only
    # WARNING: This should NEVER be used in production
    return 'django-insecure-LOCAL-DEV-ONLY-m#v&3r-oet$@=^o(vv47^s)z1s)l@#)_&)&!5o8r7x&z*q@7)e'

